PRAGMA defer_foreign_keys=TRUE;
CREATE TABLE IF NOT EXISTS "business_brand_product" (
	`id` integer PRIMARY KEY AUTOINCREMENT NOT NULL,
	`business_id` integer NOT NULL,
	`product_id` integer NOT NULL,
	`brand_id` integer NOT NULL,
	FOREIGN KEY (`business_id`) REFERENCES `businesses`(`id`) ON UPDATE cascade ON DELETE cascade,
	FOREIGN KEY (`product_id`) REFERENCES `products`(`id`) ON UPDATE cascade ON DELETE cascade,
	FOREIGN KEY (`brand_id`) REFERENCES `brands`(`id`) ON UPDATE cascade ON DELETE cascade
);
CREATE TABLE IF NOT EXISTS "contacts" (
	`id` integer PRIMARY KEY AUTOINCREMENT NOT NULL,
	`business_id` integer NOT NULL,
	`type` text NOT NULL,
	`value` text NOT NULL,
	FOREIGN KEY (`business_id`) REFERENCES `businesses`(`id`) ON UPDATE cascade ON DELETE cascade
);
CREATE TABLE IF NOT EXISTS "product_brand" (
	`id` integer PRIMARY KEY AUTOINCREMENT NOT NULL,
	`product_id` integer NOT NULL,
	`brand_id` integer NOT NULL,
	FOREIGN KEY (`product_id`) REFERENCES `products`(`id`) ON UPDATE cascade ON DELETE cascade,
	FOREIGN KEY (`brand_id`) REFERENCES `brands`(`id`) ON UPDATE cascade ON DELETE cascade
);
CREATE TABLE IF NOT EXISTS "users" (
	`id` integer PRIMARY KEY AUTOINCREMENT NOT NULL,
	`name` text NOT NULL,
	`email` text NOT NULL,
	`password` text NOT NULL,
	`img` text,
	`role` text DEFAULT 'user',
	`type` text DEFAULT 'standard',
	`created_at` integer DEFAULT '"2025-09-15T19:37:24.391Z"'
);
DELETE FROM sqlite_sequence;
