INSERT INTO brands VALUES(1,'Acer','Acer is a global leader in computing, communications, and electronics technology. The company is known for its high-quality laptops, monitors, and innovative gadgets designed to enhance productivity and entertainment.','/media/acer.png','acer');
INSERT INTO brands VALUES(2,'Hisense','Hisense specializes in cutting-edge electronics and home appliances. With a focus on smart TVs and refrigeration, Hisense offers products that combine modern aesthetics with advanced technology.','/media/hisense.png','hisense');
INSERT INTO brands VALUES(3,'LG','LG Electronics is a household name for innovation, providing a wide range of home appliances, electronics, and mobile solutions that enrich everyday life.','/media/lg.png','lg');
INSERT INTO brands VALUES(4,'MI','MI, by Xiaomi, is synonymous with smart technology, offering a comprehensive lineup of smartphones, smart home devices, and lifestyle products that deliver excellent value and quality.','/media/mi.png','mi');
INSERT INTO brands VALUES(5,'Panasonic','Panasonic is committed to creating a better life and a better world through its comprehensive product range, which includes home electronics, kitchen appliances, and industrial solutions.','/media/panasonic.png','panasonic');
INSERT INTO brands VALUES(6,'Philips','Philips is a global leader in health technology and consumer electronics. From lighting solutions to personal care products, Philips focuses on innovations that make life easier and healthier.','/media/philips.png','philips');
INSERT INTO brands VALUES(7,'Samsung','Samsung is a world-renowned brand known for its cutting-edge smartphones, televisions, and appliances. It strives to push the boundaries of innovation to create products that improve daily life.','/media/samsung.png','samsung');
INSERT INTO brands VALUES(8,'Sony','Sony Corporation is a leader in entertainment and technology, offering high-quality electronics, gaming consoles, and imaging products that cater to a global audience.','/media/sony.png','sony');
INSERT INTO brands VALUES(9,'Toshiba','Toshiba is dedicated to creating technologies and products that enhance daily living, with a focus on electronics, energy solutions, and digital products.','/media/toshiba.png','toshiba');
